mapper.select:
SELECT m.member_id,m.member_photo,m.member_idx,b.board_idx,b.board_title,b.board_contents,p.photo_name,m.member_photo,b.board_cnt,b.board_comment,b.board_like,b.board_scrap,b.board_regdate,b.category_idx,b.category_idx2,r.category_room,s.category_space
FROM pgp.member_info m LEFT JOIN pgp.board b ON m.member_idx = b.member_idx
LEFT JOIN pgp.board_photo p ON p.board_idx = b.board_idx LEFT JOIN 
pgp.category_room r on r.category_idx = b.category_idx LEFT JOIN

pgp.category_space s on s.category_idx = b.category_idx2 ;

pgp.category_space s on s.category_idx = b.category_idx2;
